mapper.select:
SELECT *
FROM T_USER

	
		AND USERNAME LIKE CONCAT('%','${usernamelike}','%' )
	
	
		AND CREATETIME >= #{startTime}
	
	
		AND CREATETIME <= #{endTime}
	

ORDER BY ID

mapper.update:
UPDATE T_USER SET
DESC=#{desc} ,ADMIN=#{admin}

	,PASSWORD=#{password}

WHERE
ID=#{id}
